using OfficeOpenXml;\nusing System.IO;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string inputPath = @"C:\path\to\your\report.xlsx";\n        string outputPath = @"C:\path\to\your\protected_report.xlsx";\n        string password = "YourPassword";\n\n        FileInfo existingFile = new FileInfo(inputPath);\n        using (ExcelPackage package = new ExcelPackage(existingFile))\n        {\n            package.Workbook.Protection.SetPassword(password);\n            FileInfo protectedFile = new FileInfo(outputPath);\n            package.SaveAs(protectedFile);\n        }\n\n        Console.WriteLine("Excel file protected successfully.");\n    }\n}