using System;\nusing System.Reflection;\n\npublic class Example {\n    [DefaultMember("SayHello")]\n    public void SayHello() {\n        Console.WriteLine("Hello!");\n    }\n\n    public static void TestInvocation() {\n        object instance = Activator.CreateInstance(typeof(Example));\n        Type type = typeof(Example);\n        \n        // Calling the default member\n        type.InvokeMember(null, BindingFlags.InvokeMethod | BindingFlags.Public | BindingFlags.Instance, null, instance, new object[] { });\n    }\n}\n\npublic class Program {\n    public static void Main(string[] args) {\n        Example.TestInvocation();  // Outputs: Hello!\n    }\n}