private void dataGridView1_KeyDown(object sender, KeyEventArgs e)\n{\n    // Check if the Enter key is pressed\n    if (e.KeyCode == Keys.Enter && !e.Shift)\n    {\n        // Get current position of selected cell\n        int currentRow = dataGridView1.CurrentCell.RowIndex;\n        int currentColumn = dataGridView1.CurrentCell.ColumnIndex;\n\n        // Move to the next column, wrapping around if necessary\n        int newColumn = (currentColumn + 1) % dataGridView1.Columns.Count;\n\n        // Determine the row index for the new cell\n        int newRow = currentRow;\n        if (newColumn == 0) // If we've reached the end of a row\n        {\n            newRow++;\n            if (newRow >= dataGridView1.Rows.Count)\n            {\n                newRow = 0; // Optionally, wrap around to the first row\n            }\n        }\n\n        // Set focus to the new cell\n        dataGridView1.CurrentCell = dataGridView1[newColumn, newRow];\n\n        // Prevent default action of moving to the next row\n        e.Handled = true;\n    }\n}