using System;\nusing System.DirectoryServices;\n\npublic class ActiveDirectoryExample\n{\n    public void SearchActiveDirectory()\n    {\n        string ldapPath = "LDAP://YOUR.DOMAIN.CONTROLER";\n        string username = "DOMAIN\\username";\n        string password = "password";\n\n        // Create a DirectoryEntry object with credentials\n        using (DirectoryEntry entry = new DirectoryEntry(ldapPath, username, password))\n        {\n            using (DirectorySearcher searcher = new DirectorySearcher(entry) { Filter = "(objectClass=user)" })\n            {\n                var results = searcher.FindAll();\n                foreach (SearchResult result in results)\n                {\n                    Console.WriteLine(result.Properties["distinguishedName"][0]);\n                }\n            }\n        }\n    }\n}