// Assuming MainForm and SecondaryForm are your forms, and MainDialog is the modal dialog.\npublic partial class MainForm : Form\n{\n    private SecondaryForm secondaryForm;\n\n    public MainForm()\n    {\n        InitializeComponent();\n        secondaryForm = new SecondaryForm();\n        secondaryForm.Show(this); // Set owner of SecondaryForm to MainForm\n    }\n\n    private void ShowModalDialogButton_Click(object sender, EventArgs e)\n    {\n        using (var modalDialog = new MainDialog())\n        {\n            modalDialog.ShowDialog(this); // Set owner of the modal dialog to MainForm\n        }\n    }\n}