using System.Data;\nusing System.Data.SqlClient;\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = "your-connection-string-here";\n        \n        using (SqlConnection connection = new SqlConnection(connectionString))\n        {\n            SqlDataAdapter adapter = new SqlDataAdapter("SELECT * FROM YourTableName", connection);\n            \n            // Fill the DataSet schema without data\n            DataSet dataSet = new DataSet();\n            adapter.FillSchema(dataSet, SchemaType.Source);\n\n            // Optionally, fill schemas for other tables and set up relations\n            string[] tableNames = { "Table1", "Table2", "Table3" }; // Add all your table names here\n            \n            foreach (var tableName in tableNames)\n            {\n                SqlDataAdapter currentAdapter = new SqlDataAdapter($"SELECT * FROM {tableName}", connection);\n                DataTable dataTable = dataSet.Tables.Add(tableName);\n                currentAdapter.FillSchema(dataSet, SchemaType.Source, tableName);\n            }\n\n            // Set up relations between tables if needed\n            DataColumn parentColumn = dataSet.Tables["ParentTableName"].Columns["ParentColumnName"];\n            DataColumn childColumn = dataSet.Tables["ChildTableName"].Columns["ChildColumnName"];\n            \n            DataRelation relation = new DataRelation("RelationName", parentColumn, childColumn);\n            dataSet.Relations.Add(relation);\n\n            // Now your DataSet contains the schema for all tables including their constraints and relations\n        }\n    }\n}