protected void DownloadFileButton_Click(object sender, EventArgs e)\n{\n    // Clear any existing content and headers\n    Response.Clear();\n    Response.ClearHeaders();\n\n    // Set the correct content type\n    string filePath = Server.MapPath("~/path/to/your/file.ext");\n    byte[] fileBytes = System.IO.File.ReadAllBytes(filePath);\n    Response.ContentType = "application/octet-stream";  // Change to appropriate type if necessary\n\n    // Add a header for the file name (useful for downloading)\n    Response.AddHeader("Content-Disposition", "attachment; filename=\"file.ext\"");\n\n    // Write the binary data to the response\n    Response.WriteBytes(fileBytes);\n\n    // End the response so no additional content is sent\n    Response.End();\n}