private static void TestCreatePost()\n{\n    Console.WriteLine("testservice.svc/create POST:");\n    Console.WriteLine("-----------------------");\n\n    Uri address = new Uri("http://localhost:" + PORT + "/testweb/testservice.svc/create");\n\n    // Create the web request  \n    HttpWebRequest request = WebRequest.Create(address) as HttpWebRequest;\n\n    // Set type to POST  \n    request.Method = "POST";\n    request.ContentType = "application/json";  // Correct content type for JSON\n\n    // Create the data we want to send  \n    string data = "{\"SomeData\":\"someTestData\"}";\n\n    // Create a byte array of the data we want to send  \n    byte[] byteData = UTF8Encoding.UTF8.GetBytes(data);\n\n    // Set the content length in the request headers  \n    request.ContentLength = byteData.Length;\n\n    // Write data  \n    using (Stream postStream = request.GetRequestStream())\n    {\n        postStream.Write(byteData, 0, byteData.Length);\n    }\n\n    try\n    {\n        // Get response  \n        using (HttpWebResponse response = request.GetResponse() as HttpWebResponse)\n        {\n            // Get the response stream  \n            StreamReader reader = new StreamReader(response.GetResponseStream());\n\n            // Console application output  \n            Console.WriteLine(reader.ReadToEnd());\n        }\n    }\n    catch (WebException e)  // Handle exception to see more details about Bad Request\n    {\n        Console.WriteLine("Error: " + e.Message);\n        if (e.Response != null)\n        {\n            using (var errorResponse = (HttpWebResponse)e.Response)\n            {\n                using (var reader = new StreamReader(errorResponse.GetResponseStream()))\n                {\n                    string errorText = reader.ReadToEnd();\n                    Console.WriteLine("Detail Error: " + errorText);\n                }\n            }\n        }\n    }\n\n    Console.WriteLine();\n    Console.WriteLine();\n}