private void checkedListBox1_ItemCheck(object sender, ItemCheckEventArgs e)\n{\n    // Define which indices should be uncheckable\n    int[] uncheckableIndices = { 0, 2 }; // For example, make items at index 0 and 2 uncheckable\n\n    if (uncheckableIndices.Contains(e.Index))\n    {\n        // Cancel the check action for these items\n        e.NewValue = e.CurrentValue;\n    }\n}