protected void Page_Load(object sender, EventArgs e)\n{\n    using (var context = new MyDbContext())\n    {\n        var data = context.MyTable.Select(t => new \n            { \n                t.Id, \n                t.ColumnName // If ColumnName is changed or removed from schema, this will cause compile-time error \n            }).ToList();\n        \n        GridView1.DataSource = data;\n        GridView1.DataBind();\n    }\n}