using System.Security;\nusing System.Security.Permissions;\n\npublic class SecurityDemo\n{\n    public static void CheckPermissions()\n    {\n        try\n        {\n            new PermissionSet(PermissionState.None).Demand(); // This should always fail if any permission is needed.\n            Console.WriteLine("All demands succeeded.");\n        }\n        catch (SecurityException ex)\n        {\n            Console.WriteLine($"Security Exception: {ex.Message}");\n        }\n\n        // Check specific permissions like file I/O\n        try\n        {\n            new FileIOPermission(FileIOPermissionAccess.AllAccess, @"C:\MyFile.txt").Demand();\n            Console.WriteLine("FileIOPermission Demand succeeded.");\n        }\n        catch (SecurityException ex)\n        {\n            Console.WriteLine($"FileIOPermission Demand failed: {ex.Message}");\n        }\n\n        // Check web permissions or others as needed\n    }\n}