using Microsoft.Win32;\nusing System;\n\npublic class ComChecker\n{\n    public static bool IsComObjectRegistered(string progId)\n    {\n        // Check if the ProgID exists in the registry under CLSID\n        string keyPath = $"CLSID\\{{{progId}}}";\n        using (RegistryKey subkey = Registry.ClassesRoot.OpenSubKey(keyPath))\n        {\n            return subkey != null;\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        string progId = "Foo.Bar"; // Example ProgID of the COM object\n        bool isRegistered = IsComObjectRegistered(progId);\n\n        if (isRegistered)\n        {\n            Console.WriteLine($"The COM object with ProgID '{progId}' is registered.");\n        }\n        else\n        {\n            Console.WriteLine($"The COM object with ProgID '{progId}' is NOT registered.");\n        }\n    }\n}