using System;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        var dllPath = Path.Combine(AppDomain.CurrentDomain.BaseDirectory,\n            Environment.Is64BitProcess ? "YourCppDll_x64.dll" : "YourCppDll_x86.dll");\n\n        if (!File.Exists(dllPath))\n        {\n            throw new FileNotFoundException("DLL not found", dllPath);\n        }\n\n        try\n        {\n            var assembly = System.Reflection.Assembly.LoadFrom(dllPath);\n            // Proceed with using the DLL through reflection or DllImport\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"Failed to load DLL: {ex.Message}");\n        }\n    }\n}