public static class DatabaseHelper\n{\n    private static string GetConnectionString()\n    {\n        // Retrieve the connection string from configuration (e.g., appsettings.json or web.config)\n        return ConfigurationManager.ConnectionStrings["YourConnectionStringName"].ConnectionString;\n    }\n\n    public static void ExecuteNonQuery(string procName, IEnumerable<StoredProcParam> parameters)\n    {\n        using (var connection = new OracleConnection(GetConnectionString()))\n        {\n            using (var command = new OracleCommand(procName, connection))\n            {\n                command.CommandType = CommandType.StoredProcedure;\n\n                // Add stored procedure parameters\n                foreach (var parameter in parameters)\n                {\n                    var cmdParameter = command.CreateParameter();\n                    cmdParameter.ParameterName = parameter.Name;\n                    cmdParameter.Value = parameter.Value;\n                    cmdParameter.OracleDbType = parameter.DbType;\n                    command.Parameters.Add(cmdParameter);\n                }\n\n                connection.Open();\n                command.ExecuteNonQuery();\n            }\n        }\n    }\n}\n\npublic class StoredProcParam\n{\n    public string Name { get; set; }\n    public object Value { get; set; }\n    public OracleDbType DbType { get; set; }\n}\n\n// Usage in your DAL or from BLL when deleting a contact\nDatabaseHelper.ExecuteNonQuery(\n    "PKG_DATA_ACCESS.DeleteContact",\n    new List<StoredProcParam>\n    {\n        new StoredProcParam("p_contact_id", contact_id, OracleDbType.Int32),\n        new StoredProcParam("p_contact_timestamp", contact_timestamp, OracleDbType.Date)\n    }\n);