using System;\nusing System.Diagnostics;\nusing System.IO;\n\nclass Program\n{\n    static void Main()\n    {\n        string pathToDlls = @"C:\Path\To\Dlls"; // Change this to your DLL path\n        var dllFiles = Directory.GetFiles(pathToDlls, "*.dll");\n        \n        Stopwatch stopwatch = new Stopwatch();\n        stopwatch.Start();\n\n        foreach (var file in dllFiles)\n        {\n            Console.WriteLine("Loading: " + Path.GetFileName(file));\n            try\n            {\n                var assembly = System.Reflection.Assembly.LoadFrom(file);\n            }\n            catch (Exception ex)\n            {\n                Console.WriteLine($"Error loading {file}: {ex.Message}");\n            }\n        }\n\n        stopwatch.Stop();\n        Console.WriteLine("Total load time: " + stopwatch.ElapsedMilliseconds + " ms");\n    }\n}