using System.Diagnostics;\n\npublic void StartProcess()\n{\n    Process myprocess = new Process();\n    // Configure your process start info here, e.g.\n    myprocess.StartInfo.FileName = "yourapp.exe";\n\n    // Attach the Exited event handler before starting the process\n    myprocess.Exited += new EventHandler(myprocess_Exited);\n\n    // Enable raising events for the process\n    myprocess.EnableRaisingEvents = true;\n\n    // Start the process\n    myprocess.Start();\n}\n\nprivate void myprocess_Exited(object sender, EventArgs e)\n{\n    lblmsg.Visible = true;\n    lblmsg.Text = "Process end...";\n}