using System.Net;\nusing System.Net.Mail;\n\nclass Program\n{\n    static void Main()\n    {\n        SmtpClient client = new SmtpClient("smtp.yourprovider.com", 587)\n        {\n            Credentials = new NetworkCredential("yourUsername", "yourPassword"),\n            EnableSsl = true,\n        };\n\n        MailMessage mailMessage = new MailMessage\n        {\n            From = new MailAddress("noreply@yourdomain.com"),\n            Subject = "Anonymous Email",\n            Body = "This is an anonymous email sent from the application."\n        };\n\n        // Add recipient email address\n        mailMessage.To.Add(new MailAddress("recipient@example.com"));\n\n        try\n        {\n            client.Send(mailMessage);\n            Console.WriteLine("Email Sent Successfully!");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Failed to send email. Error: " + ex.Message);\n        }\n    }\n}