using System;\nusing System.IO.Ports;\n\nclass Program\n{\n    static void Main()\n    {\n        // Define serial port settings\n        string portName = "COM1"; // Change to your actual port name\n        int baudRate = 9600; // Adjust as needed for your sensor\n\n        using (SerialPort serialPort = new SerialPort(portName, baudRate))\n        {\n            try\n            {\n                // Configure DTR and other properties if necessary\n                serialPort.DtrEnable = true;\n                serialPort.Parity = Parity.None;\n                serialPort.StopBits = StopBits.One;\n                serialPort.Handshake = Handshake.None;\n\n                // Open the port\n                serialPort.Open();\n\n                // Perform communication with the sensor here\n\n            }\n            catch (UnauthorizedAccessException ex)\n            {\n                Console.WriteLine($"Access to {portName} is denied. ({ex.Message})");\n            }\n            catch (IOException ex)\n            {\n                Console.WriteLine($"IO error occurred: ({ex.Message})");\n            }\n            finally\n            {\n                // Ensure the port is closed if it was opened\n                if (serialPort.IsOpen) serialPort.Close();\n            }\n        }\n    }\n}