public static void RegisterRoutes(RouteCollection routes)\n{\n    routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n    // Add route debugger if you are in debug mode\n#if DEBUG\n    RouteDebugger.RoutesDebugAttribute.Enable = true;\n#endif\n\n    routes.MapRoute(\n        "Default",\n        "{controller}/{action}/{id}",\n        new { controller = "Public", action = "Index", id = UrlParameter.Optional }\n    );\n\n    routes.MapRoute(\n        "Report1",\n        "Report/{action}/{start}/{end}",\n        new { controller = "Report", action = "Index" }\n    );\n\n    routes.MapRoute(\n        "Report2",\n        "Report/{action}/{start}/{end}/{idList}",\n        new { controller = "Report", action = "Index" }\n    );\n}