public ActionResult GetSimilarIssues(string title)\n{\n    var issues = _db.GetSimilarIssues(title).ToList();\n    \n    if (string.IsNullOrEmpty(title) || !issues.Any())\n        return Json(new { data = "NO DATA" }, JsonRequestBehavior.AllowGet);\n\n    // Using StringBuilder for efficient string concatenation and to avoid XSS\n    var htmlBuilder = new System.Text.StringBuilder();\n\n    foreach (var issue in issues)\n    {\n        htmlBuilder.AppendFormat(\n            "<div class='answer-summary' style='width: 610px;'>" +\n            "<a href='Issue.aspx?projid={0}&issuetypeid={1}&issueid={2}'>{3}</a>" +\n            "</div>",\n            System.Web.HttpUtility.HtmlEncode(issue.ProjectId.ToString()),\n            System.Web.HttpUtility.HtmlEncode(issue.IssueTypeId.ToString()),\n            System.Web.HttpUtility.HtmlEncode(issue.IssueId.ToString()),\n            System.Web.HttpUtility.HtmlEncode(issue.Summary)\n        );\n    }\n    \n    return Json(new { data = htmlBuilder.ToString() }, JsonRequestBehavior.AllowGet);\n}