using System;\nusing System.Net.Sockets;\nusing System.Text;\n\npublic class TelnetAdminService\n{\n    private TcpListener _serverSocket;\n\n    public void Start(int port)\n    {\n        _serverSocket = new TcpListener(System.Net.IPAddress.Any, port);\n        _serverSocket.Start();\n        Console.WriteLine("Telnet Admin Service started. Listening on port: " + port);\n\n        while (true)\n        {\n            TcpClient client = _serverSocket.AcceptTcpClient();\n            HandleClientCommunication(client);\n        }\n    }\n\n    private void HandleClientCommunication(TcpClient client)\n    {\n        NetworkStream stream = client.GetStream();\n        byte[] buffer = new byte[1024];\n        string commands;\n\n        try\n        {\n            int bytesRead;\n            while ((bytesRead = stream.Read(buffer, 0, buffer.Length)) != 0)\n            {\n                commands = Encoding.ASCII.GetString(buffer, 0, bytesRead);\n                Console.WriteLine("Received command: " + commands);\n\n                // Process commands here\n                string response = ExecuteCommands(commands) + "\n";\n                byte[] sendBuffer = Encoding.ASCII.GetBytes(response);\n                stream.Write(sendBuffer, 0, sendBuffer.Length);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error in HandleClientCommunication: " + ex.Message);\n        }\n        finally\n        {\n            client.Close();\n        }\n    }\n\n    private string ExecuteCommands(string commands)\n    {\n        // Implement your command handling logic here\n        if (commands.StartsWith("shutdown"))\n        {\n            return "Shutting down..."; // Implement actual shutdown logic\n        }\n        else if (commands.StartsWith("status"))\n        {\n            return "Service is running."; // Return status information\n        }\n        else\n        {\n            return "Unknown command.";\n        }\n    }\n\n    public static void Main(string[] args)\n    {\n        TelnetAdminService service = new TelnetAdminService();\n        service.Start(4000);\n    }\n}