// GET: /Widget/Edit/1\npublic ActionResult Edit(int id) {\n    var widget = _widgetService.GetWidgetByID(id);\n    var employees = _widgetService.GetAllEmployees();\n\n    var viewModel = new WidgetFormViewModel()\n                {\n                    EmployeeList = new SelectList(employees, "ID", "Name", widget.Employee),\n                    Widget = widget,\n                    WidgetID = widget.ID\n                };\n    return View("Edit", viewModel);\n}\n\n// POST: /Widget/Edit\n[HttpPost]\npublic ActionResult Edit(WidgetFormViewModel viewModel) {\n    if (ModelState.IsValid)\n    {\n        var existingWidget = _widgetService.GetWidgetByID(viewModel.WidgetID);\n        existingWidget.Employee = _widgetService.GetEmployeeByID(viewModel.EmployeeID);\n\n        try { \n            /* Save widget to DB */ \n            return RedirectToAction("List");\n        } catch {\n            /* Handle exceptions */\n        }\n    }\n\n    // Repopulate the EmployeeList for the view\n    viewModel.EmployeeList = new SelectList(_widgetService.GetAllEmployees(), "ID", "Name", viewModel.Widget.Employee);\n\n    // Return the ViewModel back to the same view with errors.\n    return View("Edit", viewModel);                 \n}