using (SqlConnection connection = new SqlConnection(connectionString))\n{\n    using (SqlCommand command = new SqlCommand("WAITFOR DELAY '00:01:30'; SELECT * FROM YourTable WHERE SomeColumn = 'SomeValue'", connection))\n    {\n        command.CommandTimeout = 10; // Timeout after 10 seconds\n        try\n        {\n            connection.Open();\n            SqlDataReader reader = command.ExecuteReader();\n            while (reader.Read())\n            {\n                // Process data if necessary\n            }\n        }\n        catch (SqlException ex)\n        {\n            if (ex.Number == -2) // SQL Server error number for timeout\n            {\n                Console.WriteLine("Timeout exception caught: " + ex.Message);\n            }\n        }\n    }\n}