using System;\nusing System.Threading;\n\nclass Program\n{\n    private static bool keepRunning = true;\n    private static Timer timer;\n\n    static void Main()\n    {\n        // Disable control-C handling by the console\n        Console.TreatControlCAsInput = true;\n\n        // Set up a simple timer to simulate periodic tasks\n        timer = new Timer(ExecuteAction, null, 0, 1000); // Every second\n\n        while (keepRunning)\n        {\n            if (Console.KeyAvailable)\n            {\n                var keyInfo = Console.ReadKey(true); // Read the key but do not display it\n                switch (keyInfo.KeyChar)\n                {\n                    case 'P':\n                        timer.Change(Timeout.Infinite, Timeout.Infinite); // Stop the timer\n                        Console.WriteLine("Paused");\n                        break;\n                    case 'C':\n                        timer.Change(0, 1000); // Restart the timer\n                        Console.WriteLine("Continued");\n                        break;\n                    case 'Q':\n                        keepRunning = false; // Exit loop and exit application\n                        Console.WriteLine("Exiting...");\n                        break;\n                }\n            }\n        }\n\n        timer.Dispose();\n    }\n\n    static void ExecuteAction(object state)\n    {\n        Console.WriteLine("Executing periodic action...");\n    }\n}