using Microsoft.EntityFrameworkCore;\n\npublic class MyDbContext : DbContext\n{\n    public DbSet<MyCustomObject> CustomObjects { get; set; }\n\n    protected override void OnConfiguring(DbContextOptionsBuilder optionsBuilder)\n    {\n        optionsBuilder.UseOracle("YourConnectionStringHere");\n    }\n\n    protected override void OnModelCreating(ModelBuilder modelBuilder)\n    {\n        // Example of custom mapping using Fluent API\n        modelBuilder.Entity<MyCustomObject>()\n            .ToTable("MY_TABLE")\n            .HasKey(e => e.Id);\n\n        modelBuilder.Entity<MyCustomObject>()\n            .Property(e => e.CustomProperty)\n            .HasColumnName("CUSTOM_PROPERTY_IN_DB");\n    }\n}\n\npublic class MyCustomObject\n{\n    public int Id { get; set; }\n    public string CustomProperty { get; set; }\n}