using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\nclass Program\n{\n    static void Main()\n    {\n        XmlDocument xdoc = new XmlDocument();\n        xdoc.LoadXml(@"<?xml version=""1.0"" encoding=""utf-8"" ?>\n                        <myXml xmlns=""http://MyNamespace"">\n                        <data>Hello World</data>\n                    </myXml>");\n\n        XPathNavigator navigator = xdoc.CreateNavigator();\n\n        // Define the namespace manager if there are any namespaces\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(xdoc.NameTable);\n        nsmgr.AddNamespace("ns", "http://MyNamespace");\n\n        // XPath 1.0 expression to mimic ends-with logic\n        string xpathExpression = @"substring(/ns:myXml/ns:data, string-length(/ns:myXml/ns:data) - string-length('World') + 1) = 'World'";\n\n        XPathExpression xpr = navigator.Compile(xpathExpression);\n        xpr.SetContext(nsmgr);\n\n        object result = navigator.Evaluate(xpr);\n        Console.WriteLine(result); // Output should be True\n    }\n}