using System;\nusing System.DirectoryServices;\n\npublic void AddUserToGroup(string groupPath, string userPath, string userName, string password)\n{\n    try\n    {\n        using (DirectoryEntry domain = new DirectoryEntry(groupPath, userName, password))\n        {\n            using (DirectoryEntry user = new DirectoryEntry(userPath))\n            {\n                domain.Properties["member"].Add(user);\n                domain.CommitChanges();\n            }\n        }\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine($"Error: {ex.Message}");\n    }\n}