using System;\nusing System.Diagnostics;\n\nclass BandwidthMonitor\n{\n    private readonly PerformanceCounter _receiveBytesPerSec;\n    private readonly PerformanceCounter _sentBytesPerSec;\n    \n    public BandwidthMonitor()\n    {\n        // Initialize performance counters for monitoring network traffic.\n        _receiveBytesPerSec = new PerformanceCounter("Network Interface", "Received Bytes/sec", true);\n        _sentBytesPerSec = new PerformanceCounter("Network Interface", "Sent Bytes/sec", true);\n    }\n\n    public void DisplayCurrentBandwidthUsage()\n    {\n        Console.WriteLine($"Receive: {_receiveBytesPerSec.NextValue()} bytes/second");\n        Console.WriteLine($"Send: {_sentBytesPerSec.NextValue()} bytes/second");\n    }\n    \n    static void Main(string[] args)\n    {\n        var monitor = new BandwidthMonitor();\n        while (true)\n        {\n            monitor.DisplayCurrentBandwidthUsage();\n            System.Threading.Thread.Sleep(1000); // Update every second\n        }\n    }\n}