[Serializable]\n[DataContract]\npublic class SomeClass\n{\n    [DataMember]\n    public string PropertyA { get; set; }\n\n    // Use IgnoreDataMember to prevent WCF from attempting to serialize this property directly\n    [IgnoreDataMember]\n    public string PropertyB { get; private set; }\n\n    // This will hold the value during serialization\n    private string _propertyB;\n\n    // Ensure the property is serialized by using OnSerializing and OnDeserialized\n    [OnSerializing]\n    void OnSerializing(StreamingContext context)\n    {\n        _propertyB = PropertyB;\n    }\n\n    [OnDeserialized]\n    void OnDeserialized(StreamingContext context)\n    {\n        PropertyB = _propertyB;\n    }\n}