using System;\nusing System.Configuration;\n\nclass Program\n{\n    static void Main()\n    {\n        string configFilePath = @"C:\path\to\web.config";\n        ExeConfigurationFileMap configFileMap = new ExeConfigurationFileMap { ExeConfigFilename = configFilePath };\n        Configuration config = ConfigurationManager.OpenMappedExeConfiguration(configFileMap, ConfigurationUserLevel.None);\n\n        // Example: Change a key in <appSettings>\n        if (config.AppSettings.Settings["SettingKey"] != null)\n            config.AppSettings.Settings["SettingKey"].Value = "New Value";\n        else\n            config.AppSettings.Settings.Add("SettingKey", "New Value");\n\n        // Example: Add a connection string\n        ConnectionStringsSection connectionStringsSection = config.ConnectionStrings;\n        if (connectionStringsSection.ConnectionStrings["MyConnectionString"] == null)\n            connectionStringsSection.ConnectionStrings.Add(new ConnectionStringSettings("MyConnectionString", "Data Source=.;Initial Catalog=DBName;Integrated Security=True"));\n\n        // Save the configuration file.\n        config.Save(ConfigurationSaveMode.Modified);\n    }\n}