using System;\nusing Microsoft.Web.Administration;\n\nclass Program\n{\n    static void Main()\n    {\n        ServerManager server = new ServerManager();\n\n        // Start an Application Pool in IIS7+\n        ApplicationPool appPool = server.ApplicationPools["YourAppPoolName"];\n        if (!appPool.State.Equals(ObjectState.Started))\n        {\n            appPool.Start();\n        }\n\n        // Stop an Application Pool\n        if (appPool.State.Equals(ObjectState.Running))\n        {\n            appPool.Stop();\n        }\n\n        // Restart an Application Pool\n        appPool.Recycle();\n\n        server.CommitChanges();\n    }\n}