using System;\nusing System.Management;\n\nclass Program\n{\n    static void Main()\n    {\n        // Start an Application Pool in IIS6\n        ManagementObjectSearcher searcher = new ManagementObjectSearcher("SELECT * FROM IIsApplicationPool WHERE Name='YourAppPoolName'");\n        foreach (ManagementObject pool in searcher.Get())\n        {\n            pool.InvokeMethod("Start", null);\n        }\n    }\n}