using HtmlAgilityPack;\n\nvar url = "http://example.com/page-with-tables";\nvar web = new HtmlWeb();\nvar doc = web.Load(url);\n\n// Select all tables from the document\nvar tables = doc.DocumentNode.SelectNodes("//table");\n\nif (tables != null)\n{\n    foreach (HtmlNode table in tables)\n    {\n        // Iterate over rows in a table\n        var rowCollection = table.SelectNodes(".//tr");\n        if (rowCollection != null)\n        {\n            foreach (HtmlNode row in rowCollection)\n            {\n                // Extract individual cells from the row\n                var cellCollection = row.SelectNodes(".//td|.//th");\n                if (cellCollection != null)\n                {\n                    foreach (HtmlNode cell in cellCollection)\n                    {\n                        Console.Write(cell.InnerText.Trim() + "\t"); // Print each cell's content\n                    }\n                    Console.WriteLine(); // New line after each row\n                }\n            }\n        }\n    }\n}\nelse\n{\n    Console.WriteLine("No tables found.");\n}