<% using (Ajax.BeginForm("SimpleUpdate", null, \n        new AjaxOptions \n        { \n            UpdateTargetId = "ajaxPanel", \n            InsertionMode = InsertionMode.Replace // Ensure only replacement\n        }))\n{ %>\n    <%= Html.DropDownList("CarMake", (SelectList)ViewData["CarMake"]) %>\n    <br />\n    <input type="submit" value="SimpleUpdate" />\n<% } %>\n\n<div id="ajaxPanel">\n    <% if (Model != null)\n       { \n           Html.RenderPartial("_carmodelsPartial", Model); // Use RenderPartial here for initial rendering\n       }\n    %>\n</div>