protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Initialize and set your data source in the session on initial load\n        var dataSource = ConfigurationSettings.AppSettings["contactsDB"];\n        Session["DataSource"] = dataSource;\n    }\n}\n\nprotected void cboPeople_SelectedIndexChanged(object sender, EventArgs e)\n{\n    string dataSource = (string)Session["DataSource"]; // Retrieve from session\n    string id = People[cboPeople.Text];\n    \n    using (var objConn = new OleDbConnection(dataSource))\n    {\n        UpdateLiteral(objConn, id);\n    }\n}