using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\nclass Program\n{\n    static void Main()\n    {\n        string xmlRequest = @"<?xml version=""1.0""?>\n<command>\n    <parameters />\n</command>";\n\n        byte[] byteArray = Encoding.UTF8.GetBytes(xmlRequest);\n\n        HttpWebRequest request = (HttpWebRequest)WebRequest.Create("http://server.example.com/server/service.php");\n        request.Method = "POST";\n        request.ContentType = "text/xml"; // or "application/xml" if necessary\n        request.ContentLength = byteArray.Length;\n\n        using (Stream dataStream = request.GetRequestStream())\n        {\n            dataStream.Write(byteArray, 0, byteArray.Length);\n        }\n\n        try\n        {\n            using (HttpWebResponse response = (HttpWebResponse)request.GetResponse())\n            {\n                StreamReader reader = new StreamReader(response.GetResponseStream());\n                string responseFromServer = reader.ReadToEnd();\n                Console.WriteLine(responseFromServer);\n            }\n        }\n        catch (WebException ex)\n        {\n            Console.WriteLine(ex.Message);\n            if (ex.Response != null)\n            {\n                var errorResponse = (HttpWebResponse)ex.Response;\n                using (var reader = new StreamReader(errorResponse.GetResponseStream()))\n                {\n                    string errorText = reader.ReadToEnd();\n                    Console.WriteLine(errorText);\n                }\n            }\n        }\n    }\n}