using System;\nusing System.Windows.Forms;\n\npublic class KeyPressExample : Form {\n    private bool ctrlMPressed = false;\n\n    public KeyPressExample() {\n        this.KeyDown += new KeyEventHandler(this.Form_KeyDown);\n        this.KeyUp += new KeyEventHandler(this.Form_KeyUp);\n    }\n\n    private void Form_KeyDown(object sender, KeyEventArgs e) {\n        if (e.Control && e.KeyCode == Keys.M) {\n            ctrlMPressed = true;\n        } else if (ctrlMPressed && e.KeyCode == Keys.M) {\n            // Ctrl+M followed by M was pressed\n            MessageBox.Show("Ctrl+M M sequence detected!");\n            ctrlMPressed = false; // Reset for next attempt\n        }\n    }\n\n    private void Form_KeyUp(object sender, KeyEventArgs e) {\n        if (e.Control && e.KeyCode == Keys.M) {\n            ctrlMPressed = false;\n        }\n    }\n\n    [STAThread]\n    static void Main() {\n        Application.EnableVisualStyles();\n        Application.SetCompatibleTextRenderingDefault(false);\n        Application.Run(new KeyPressExample());\n    }\n}