using System;\nusing Microsoft.SqlServer.Dts.Runtime;\n\npublic void Main()\n{\n    // Example to access and manipulate SSIS package component metadata programmatically\n    Application app = new Application();\n    Package pkg = app.LoadPackage(@"C:\path\to\your\package.dtsx", null);\n\n    foreach (IDTSComponentMetaData100 comp in pkg.ComponentMetaDataCollection)\n    {\n        if (comp.Name == "Your Conditional Split Name")\n        {\n            Console.WriteLine("Found Conditional Split Component");\n            IDTSInput130 input = comp.InputCollection[0];\n            foreach (IDTSVirtualInputColumn90 virtCol in input.VirtualInputColumnCollection)\n            {\n                Console.WriteLine($"Virtual Column: {virtCol.Name}");\n            }\n            break;\n        }\n    }\n\n    pkg.Dispose();\n}