using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        Func<string, bool> RegexValidate = s => Regex.IsMatch(s, @"(?=(?:\D*\d\D*){7})");\n        string x = "asda 1234567 sdfasdf";\n        string y = "asda   sdfa 123456 sdfasdf";\n\n        bool xx = RegexValidate(x); //true\n        bool yy = RegexValidate(y); //false\n\n        Console.WriteLine(xx); // Output: True\n        Console.WriteLine(yy); // Output: False\n    }\n}