using System;\nusing Interop.Tapi3Lib;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        ITTAPI tapi = new TAPILib.TAPI();\n        TAPICaps tapiCaps;\n        \n        // Initialize TAPI\n        tapi.Initialize();\n        tapiCaps = (TAPICaps)tapi.GetCapability(TAPI3Lib.CAPSET_TAPI);\n\n        Console.WriteLine("TAPI Version: " + tapiCaps.dwVersion);\n        \n        // Add event handlers for call notifications here\n        \n        // Wait until user exits application\n        Console.ReadLine();\n\n        // Cleanup TAPI\n        tapi.Shutdown();\n    }\n}