foreach (DataSet1.DataTable1Row dr in ds.DataTable1)\n{\n    DataSet1.DataTable1Row foundRow = ds.DataTable1.FindById(dr.Id);\n    if (foundRow != dr || dr.RowState != DataRowState.Unchanged)\n    {\n        Console.WriteLine($"ID mismatch or row state not unchanged for ID: {dr.Id}");\n        Console.WriteLine($"Enumerated Row State: {dr.RowState}, Found Row State: {foundRow?.RowState}");\n        Console.WriteLine($"Enumerated Row Data: {string.Join(", ", dr.ItemArray)}");\n        Console.WriteLine($"Found Row Data: {string.Join(", ", foundRow?.ItemArray ?? new object[] { null })}");\n    }\n}