protected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        // Bind your data to GridViewA here\n        GridViewA.DataSource = GetData();\n        GridViewA.DataBind();\n\n        CalculateTotalPrice();\n    }\n}\n\nprivate IEnumerable<YourDataModel> GetData()\n{\n    // Method to fetch and return data\n    return new List<YourDataModel>();\n}\n\nprotected void GridViewA_RowDataBound(object sender, GridViewRowEventArgs e)\n{\n    if (e.Row.RowType == DataControlRowType.DataRow)\n    {\n        // You can manipulate the rows here if needed\n    }\n}\n\nprotected void CalculateTotalButton_Click(object sender, EventArgs e)\n{\n    CalculateTotalPrice();\n}\n\nprivate void CalculateTotalPrice()\n{\n    decimal totalPrice = 0;\n\n    foreach (GridViewRow row in GridViewA.Rows)\n    {\n        totalPrice += Convert.ToDecimal(row.Cells[/*Index of Price Column*/].Text);\n    }\n\n    TotalPriceTextBox.Text = totalPrice.ToString("N2");\n}