var stuff = from l in _db.SqlLinks\n            select new\n                   {\n                       Link = l,\n                       Rating = (from v in l.SqlLinkVotes\n                                 where v.Tag == tagId\n                                       && v.VoteDate >= since\n                                 select (int?)v.Vote).DefaultIfEmpty(0).Sum(),\n                       NumberOfVotes = (from v in l.SqlLinkVotes\n                                        where v.Tag == tagId\n                                              && v.VoteDate >= since\n                                        select v.Vote).Count(),\n                       NumberOfComments = (from v in l.SqlLinkVotes\n                                           where v.Tag == tagId\n                                                 && v.VoteDate >= since\n                                                 && !string.IsNullOrEmpty(v.Comment)\n                                           select v.Vote).Count()\n                   };