using System;\nusing System.IO;\nusing System.Linq;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string typeName = "YourTypeName"; // Replace with the name of the type you're looking for\n        string directoryPath = @"C:\path\to\your\dlls"; // Directory containing DLLs\n\n        foreach (var file in Directory.GetFiles(directoryPath, "*.dll"))\n        {\n            try\n            {\n                var assembly = Assembly.LoadFrom(file);\n                if (assembly.GetType(typeName) != null)\n                {\n                    Console.WriteLine($"Type {typeName} found in: {file}");\n                    return;\n                }\n            }\n            catch (ReflectionTypeLoadException e)\n            {\n                // Handle types that could not be loaded\n                foreach (var loaderExceptions in e.LoaderExceptions)\n                {\n                    Console.WriteLine(loaderExceptions);\n                }\n            }\n            catch (BadImageFormatException)\n            {\n                // Ignore files that are not valid assemblies\n            }\n        }\n\n        Console.WriteLine($"Type {typeName} not found in any DLLs.");\n    }\n}