using System;\nusing System.Reflection;\n\npublic class ComInstanceCreator\n{\n    public static object CreateInstanceFromFile(string dllPath, string typeName)\n    {\n        // Load the assembly from the file path\n        Assembly assembly = Assembly.LoadFrom(dllPath);\n\n        // Get the type using the type name\n        Type type = assembly.GetType(typeName);\n        \n        if (type == null)\n        {\n            throw new ArgumentException($"Type {typeName} not found in {dllPath}");\n        }\n\n        // Create an instance of the type\n        object instance = Activator.CreateInstance(type);\n\n        return instance;\n    }\n}