using System;\nusing System.ComponentModel.DataAnnotations;\n\npublic class LocalizedDateTimeAttribute : ValidationAttribute\n{\n    protected override ValidationResult IsValid(object value, ValidationContext validationContext)\n    {\n        if (value == null) return ValidationResult.Success;\n\n        if (!(value is DateTime || value is string))\n            return new ValidationResult("Invalid type. Only DateTime and strings are accepted.");\n\n        if (!DateTime.TryParse(value.ToString(), out _))\n        {\n            // Use resource files for localization\n            var errorMessage = "The_custom_localized_error_message"; // Replace with your key from resource file\n\n            // If using resource files\n            // var errorMessage = ValidationMessages.BlahIsNotValid;\n            \n            return new ValidationResult(errorMessage);\n        }\n\n        return ValidationResult.Success;\n    }\n}