using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string xml = @"<My_RootNode xmlns:xsi='http://www.w3.org/2001/XMLSchema-instance' xmlns:xsd='http://www.w3.org/2001/XMLSchema' xmlns=''>\n  <id root='2.16.840.1.113883.3.51.1.1.1' extension='someIdentifier' xmlns='urn:hl7-org:v3'/>\n  <creationTime xsi:nil='true' xmlns='urn:hl7-org:v3'/>\n</My_RootNode>";\n\n        XmlDocument myXmlDoc = new XmlDocument();\n        myXmlDoc.LoadXml(xml);\n\n        XmlNamespaceManager nsmgr = new XmlNamespaceManager(myXmlDoc.NameTable);\n        // Define a prefix for the default namespace and add it to the manager\n        nsmgr.AddNamespace("hl7", "urn:hl7-org:v3");\n\n        XmlNode idNode = myXmlDoc.SelectSingleNode("/My_RootNode/hl7:id", nsmgr);\n\n        if (idNode != null)\n        {\n            string msgID = idNode.Attributes.GetNamedItem("extension").Value;\n            Console.WriteLine("Extension attribute value: " + msgID);\n        }\n        else\n        {\n            Console.WriteLine("Node not found.");\n        }\n    }\n}