using System;\nusing System.IO;\nusing System.Reflection;\nusing log4net;\nusing log4net.Config;\n\npublic class Wrapper : IWrapper\n{\n    private static readonly ILog log = LogManager.GetLogger(typeof(Wrapper));\n\n    public Wrapper()\n    {\n        // Load configuration from config file named after the DLL.\n        var configPath = Path.ChangeExtension(Assembly.GetExecutingAssembly().Location, ".config");\n        var fileMap = new ExeConfigurationFileMap { ExeConfigFilename = configPath };\n        var assemblyConfig = ConfigurationManager.OpenMappedExeConfiguration(fileMap, ConfigurationUserLevel.None);\n\n        // Configure log4net.\n        XmlConfigurator.Configure(assemblyConfig);\n    }\n\n    public void DoSomething()\n    {\n        log.Info("This is a test log message.");\n        // Your method implementation\n    }\n}