using System.IO;\nusing System.Windows.Documents;\nusing System.Windows.Markup;\n\npublic void InsertHtmlFragmentIntoFlowDocument(FlowDocument flowDoc, string htmlFragment)\n{\n    // Convert HTML to XAML\n    var converter = new HtmlToXamlConverter();\n    string xaml = converter.ConvertHtmlToXaml(htmlFragment, true);\n\n    // Parse the XAML into a Block or Inline element\n    using (var reader = new StringReader(xaml))\n    using (XmlReader xmlReader = XmlReader.Create(reader))\n    {\n        var block = (Block)XamlReader.Load(xmlReader);\n        flowDoc.Blocks.Add(block); // Add the generated block to the FlowDocument's Blocks collection\n    }\n}