using System.IO;\n     using System.Security.AccessControl;\n\n     string directory = @"C:\Path\To\Your\Dlls";\n     DirectoryInfo dirInfo = new DirectoryInfo(directory);\n     DirectorySecurity dSecurity = dirInfo.GetAccessControl();\n\n     // Add the desired user with Read & Execute permission\n     dSecurity.AddAccessRule(new FileSystemAccessRule(@"DOMAIN\Username", FileSystemRights.ReadAndExecute, AccessControlType.Allow));\n     dirInfo.SetAccessControl(dSecurity);